    private void logar(){
       try {
            String nomeBD, senhaBD;
            nomeBD = jUsuario.getText();
            senhaBD = jPassword.getText();

            Usuarios objusuario = new Usuarios();
            objusuario.setNomeBD(nomeBD);
            objusuario.setSenhaBD(senhaBD);

            UsuarioDAO objusuariodao = new UsuarioDAO();
            ResultSet rsusuariodao = objusuariodao.autenticacaousuario(objusuario);

            if (rsusuariodao.next()) {
                // chamar tela que eu quero abrir
                Menu telamenu = new Menu();
                telamenu.setVisible(true);

                dispose();

            } else {
                //enviar mensagem dizendo incorreto
                JOptionPane.showMessageDialog(null, "Usu√°rio ou Senha inv√°lida!");
            }

        } catch (SQLException erro) {
            JOptionPane.showMessageDialog(null, erro);
        }
    }                                         

